/*创建huazan数据库*/
SET NAMES UTF8;
DROP DATABASE IF EXISTS huazan;
CREATE DATABASE huazan CHARSET=UTF8;
USE huazan;

/*创建鲜花种类*/
CREATE TABLE hz_flower_family(
	id INT PRIMARY KEY AUTO_INCREMENT,
	fname VARCHAR(32)
);
/*创建商品表*/
CREATE TABLE hz_flower(
	id INT PRIMARY KEY AUTO_INCREMENT,
	family_id INT,                     #所属鲜花种类编号
	product_id VARCHAR(16),            #鲜花编号
	title VARCHAR(128),                #标题
	img VARCHAR(128),                  #图片
	price DECIMAL(8,2),				         #价格
	materials VARCHAR(128),			       #材料
	pack VARCHAR(32),                  #包装
	presenter VARCHAR(64),             #附赠
	distribu VARCHAR(32),              #配送
	explains VARCHAR(256),             #说明
	details VARCHAR(1024),             #商品详情
	save DECIMAL(8,2)                  #节省多少钱
);
/*用户信息*/
CREATE TABLE hz_user(
	id INT PRIMARY KEY AUTO_INCREMENT,
	uname VARCHAR(32),
	upwd VARCHAR(32),
	email VARCHAR(64),
	phone VARCHAR(16) NOT NULL UNIQUE,
	avatar VARCHAR(128),               #头像图片路径
	user_name VARCHAR(32),             #用户名，如王小明
	gender INT                         #性别  0-女  1-男
); 
/*收货地址信息表*/
CREATE TABLE hz_receiver_address(
	id INT PRIMARY KEY AUTO_INCREMENT,
	user_id INT,                       #用户编号
	receiver VARCHAR(16),              #接收人姓名
	province VARCHAR(16),              #省
	city VARCHAR(16),				           #市
	county VARCHAR(16),                #县
	address VARCHAR(128),              #详细地址
	cellphone VARCHAR(16),             #手机
	fixedphone VARCHAR(16),            #固定电话
	postcode CHAR(6),                  #邮编
	tag VARCHAR(16),                   #标签名
	is_default BOOLEAN                 #是否为当前用户的默认收货地址
);
/*购物车条目*/
CREATE TABLE hz_shopping_cart(
	id INT PRIMARY KEY AUTO_INCREMENT,
	user_id INT,                       #用户编号
	product_id INT,                    #商品编号
	count INT                          #购买数量
);
/*用户订单*/
CREATE TABLE hz_order(
	id INT PRIMARY KEY AUTO_INCREMENT,
	user_id INT,                       #用户编号
	address_id INT,                    #地址编号
	status INT,                        #订单状态  1-等待付款  2-等待发货  3-运输中  4-已签收  5-已取消
	order_time BIGINT,                 #下单时间
	pay_time BIGINT,                   #付款时间
	deliver_time BIGINT,               #发货时间
	received_time BIGINT               #签收时间
);
/*用户订单详情表*/
CREATE TABLE hz_order_detail(
 	id INT PRIMARY KEY AUTO_INCREMENT,
 	order_id INT,                      #订单编号
 	product_id INT,                    #产品编号
 	count INT                          #购买数量
 );
/*首页轮播图表*/
CREATE TABLE hz_index_carousel(
	id INT PRIMARY KEY AUTO_INCREMENT,
	img VARCHAR(128),                  #图片路径
	title VARCHAR(64),                 #图片描述
	href VARCHAR(128)                  #图片链接
);
/*首页商品表*/
CREATE TABLE hz_index_product(
	id INT PRIMARY KEY AUTO_INCREMENT,
	title VARCHAR(64),                 #商品标题
	pic VARCHAR(128),                  #图片
	price DECIMAL(10,2),               #商品价格
	href VARCHAR(128)                  #连接地址
);
/*花语文章列表分类*/
CREATE TABLE hz_huayu_category(
	id INT PRIMARY KEY AUTO_INCREMENT,
	category_name VARCHAR(64),         #分类名称
	category_info VARCHAR(64)          #标题信息
);
/*花语文章页*/
CREATE TABLE hz_huayu_articles(
	id INT PRIMARY KEY AUTO_INCREMENT,
	subject VARCHAR(64),                #文章标题
	timer VARCHAR(32),                  #发表时间
	description mediumtext,             #文章内容
	img VARCHAR(50),                    #显示图片
	category_id SMALLINT(5)             #分类id
);
